-- Track buff stacks and uptime
buffStacks = {}
buffStartTime = {}

-- Berserk ignore counter
berserk_ignores = 0

-- Flesh pieces to ignore (troll regeneration mobs)
local flesh_pieces = {
  ["Bit of flesh"] = true,
  ["Mass of flesh"] = true,
  ["Mound of flesh"] = true,
  ["Severed limb"] = true,
  ["Trollkin"] = true,
  ["Wall of flesh"] = true
}


function FormatUptime(seconds)
    if seconds < 60 then
        return string.format("%ds", seconds)
    elseif seconds < 3600 then
        local mins = math.floor(seconds / 60)
        local secs = seconds % 60
        return string.format("%dm%ds", mins, secs)
    else
        local hours = math.floor(seconds / 3600)
        local mins = math.floor((seconds % 3600) / 60)
        return string.format("%dh%dm", hours, mins)
    end
end

function OnPluginInstall()
    Send("party say Enhanced BuffTracker v2.5 loaded - flesh pieces ignored!")
    for name, buff in pairs(buffDetails) do
        buffStacks[name] = 0
        buffStartTime[name] = {}

        local triggerFlags = trigger_flag.Enabled + trigger_flag.RegularExpression * (buff["regex"] and 1 or 0)

        -- Primary application trigger
        AddTriggerEx(
            "gain_" .. name:gsub("[^A-Za-z0-9_]", ""):sub(1, 30),
            buff["apply"],
            "OnBuffGain('" .. name .. "')",
            triggerFlags,
            -1, 0, "", "", sendto.script, 100
        )

        -- Alternate application trigger (for self-cast variations)
        if buff["apply_alt"] then
            AddTriggerEx(
                "gain_alt_" .. name:gsub("[^A-Za-z0-9_]", ""):sub(1, 27),
                buff["apply_alt"],
                "OnBuffGain('" .. name .. "')",
                triggerFlags,
                -1, 0, "", "", sendto.script, 100
            )
        end

        -- Fade trigger
        AddTriggerEx(
            "loss_" .. name:gsub("[^A-Za-z0-9_]", ""):sub(1, 30),
            buff["fade"],
            "OnBuffLoss('" .. name .. "')",
            triggerFlags,
            -1, 0, "", "", sendto.script, 100
        )
    end
end

function OnBuffGain(name)
    local maxStacks = buffDetails[name]["maxStacks"]

    if buffStacks[name] < maxStacks then
        buffStacks[name] = buffStacks[name] + 1
        table.insert(buffStartTime[name], os.time())

        -- Only show stack info for multi-stack buffs
        if maxStacks > 1 then
            Send("party say " .. name .. " up! Current stacks: " .. buffStacks[name])
        else
            Send("party say " .. name .. " up!")
        end
    else
        -- Only announce max stacks for multi-stack buffs
        if maxStacks > 1 then
            Send("party say " .. name .. " is at max stacks: " .. maxStacks)
        end
    end
end

function OnBuffLoss(name)
    local maxStacks = buffDetails[name]["maxStacks"]

    if buffStacks[name] and buffStacks[name] > 0 then
        local startTime = table.remove(buffStartTime[name], 1)
        local uptime = os.time() - startTime
        buffStacks[name] = buffStacks[name] - 1

        -- Special handling for Berserk - reset ignore counter when berserk ends
        if name == "Berserk" then
            if berserk_ignores > 0 then
                Note("Berserk ended. Total ignores this session: " .. berserk_ignores)
            end
            berserk_ignores = 0
        end

        -- Only show stack info for multi-stack buffs
        if maxStacks > 1 then
            Send("party say " .. name .. " lost. Stacks: " .. buffStacks[name] .. " | Was up: " .. FormatUptime(uptime))
        else
            Send("party say " .. name .. " lost. Was up: " .. FormatUptime(uptime))
        end
    else
        Send("party say " .. name .. " is not active.")
    end
end

function OnBuffStatus()
    local activeBuffs = {}
    for name, stacks in pairs(buffStacks) do
        if stacks > 0 then
            local maxStacks = buffDetails[name]["maxStacks"]
            local uptimeStr = ""
            if buffStartTime[name] and #buffStartTime[name] > 0 then
                local currentUptime = os.time() - buffStartTime[name][1]
                uptimeStr = "(" .. FormatUptime(currentUptime) .. ")"
            end

            -- Only show stack info for multi-stack buffs
            if maxStacks > 1 then
                table.insert(activeBuffs, name .. ":" .. stacks .. "/" .. maxStacks .. uptimeStr)
            else
                table.insert(activeBuffs, name .. uptimeStr)
            end
        end
    end

    if #activeBuffs > 0 then
        Send("party say Buffs: " .. table.concat(activeBuffs, " | "))
    else
        Send("party say No active buffs.")
    end
end

-- ==================== BERSERK IGNORE COUNTER ====================

function OnBerserkIgnore(name, line, wildcards)
    berserk_ignores = berserk_ignores + 1
    Note("You have ignored a total of " .. berserk_ignores .. " attacks since last reset.")
end

function OnBerserkFading(name, line, wildcards)
    -- Warning that berserk is about to drop
    -- Don't announce here - wait for death or actual fade
    Note("Berserk is fading...")
end

function OnDeathBerserkAnnounce(name, line, wildcards)
    -- Extract mob name from death message
    local mob_name = wildcards[1]

    -- Ignore troll flesh pieces (regeneration mobs)
    if flesh_pieces[mob_name] then
        return
    end

    -- Announce ignore count to party when creature dies (only if we ignored any attacks)
    -- Keep counting until berserk ends - don't reset here
    if berserk_ignores > 0 then
        Send("party say Berserk ignores: " .. berserk_ignores)
    end
end

function OnCalm()
    -- Manual calm down - announce total ignores and reset counter
    if berserk_ignores > 0 then
        Send("party say RED_COLOUR Calming with a total of " .. berserk_ignores .. " ignores.")
        berserk_ignores = 0
        Note("Ignored Attacks counter reset to zero")
    end
    Send("use calm down")
end
